REPLACE INTO `crpStatic` (`corporationID`, `corporationName`, `description`, `tickerName`, `url`, `taxRate`, `minimumJoinStanding`, `corporationType`, `hasPlayerPersonnelManager`, `sendCharTerminationMessage`, `creatorID`, `ceoID`, `stationID`, `raceID`, `allianceID`, `shares`, `memberCount`, `memberLimit`, `allowedMemberRaceIDs`, `graphicID`, `shape1`, `shape2`, `shape3`, `color1`, `color2`, `color3`, `typeface`, `division1`, `division2`, `division3`, `division4`, `division5`, `division6`, `division7`, `balance`, `deleted`) VALUES (1000179, '24th Imperial Crusade', 'It was us who brought civilization to this dark and perilous world, and it is us who uphold it, with compassion, strength and courage. We need you to help us save the Minmatar from themselves. We will help the lost ones find their way, capsuleer, and we will do it with the deathless force only you can wield.', 'IC24', '', 0.1, 0, 1, 0, 0, 0, 0, 0, 4, 0, 0, 0, 100000, 15, 3369, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1st division', '2nd division', '3rd division', '4th division', '5th division', '6th division', '7th division', 0, 0);
